/* This procedure makes-inserts a booking after it checks about the availability of  * the room. It makes use of the AutoIncrementing trigger and also of the check  * availability function * ***We suppose that each session starts exactly at an hour. eg 2 o'clock*** */CREATE OR REPLACE PROCEDUREnew_booking(	v_roomID IN INT,	v_employeeID IN INT,	v_customerID IN INT,	v_treatmentID IN INT,	v_date IN NH_BOOKING."Date"%TYPE,	v_price IN NUMBER)ASv_errors INTEGER;BEGIN		v_errors := isRoomAvailable(v_roomID, v_date);		IF (v_errors =0) THEN		--key is produced using AI trigger		INSERT INTO NH_BOOKING(RoomID, EmployeeID, CustomerID, TreatmentID, "Date", Price) 		VALUES (v_roomID, v_employeeID, v_customerID, v_treatmentID, v_date, v_price);				DBMS_OUTPUT.PUT_LINE('Booking Completed!');		DBMS_OUTPUT.PUT_LINE('Customer must pay: ' || to_char(v_price));	ELSE		DBMS_OUTPUT.PUT_LINE('This room is not available at that time. Try another one');	END IF;	EXCEPTION	WHEN OTHERS THEN	raise_application_error(-20001, 'Critical Error, call ahughes ' || SQLERRM);	END;