/* This trigger prints a message every time that stock reaches minimum = 1 */CREATE OR REPLACE TRIGGER STOCKAFTER UPDATE ON "NH_STOCK"FOR EACH ROWDECLARE	v_quantity INTEGER;	v_stockID INTEGER;  v_productID INTEGER;  v_shopID INTEGER;BEGIN		v_quantity := :NEW.Quantity;  v_stockID := :NEW.StockID;  v_productID := :NEW.ProductID;  v_shopID := :NEW.ShopID;	  --if stock under 2 items advise.	IF (:NEW.Quantity < 2) THEN		DBMS_OUTPUT.PUT_LINE('Warning, minimum amount of stock reached.');		DBMS_OUTPUT.PUT_LINE('StockID: ' || v_stockID);		DBMS_OUTPUT.PUT_LINE('ProductID: ' || v_productID);		DBMS_OUTPUT.PUT_LINE('ShopID: ' || v_shopID);	END IF;END;